CREATE TABLE IF NOT EXISTS tims_permission_category  (
    id                      INTEGER     NOT NULL PRIMARY KEY ,
    code                    TEXT        NOT NULL CHECK (char_length(code)  <= 255) UNIQUE,
    label                   TEXT        NOT NULL CHECK (char_length(label)  <= 255) UNIQUE,
    category_order          INTEGER     NOT NULL
);


CREATE SEQUENCE IF NOT EXISTS tims_permission_seq START 1000;

CREATE TABLE IF NOT EXISTS tims_permission  (
    id                      INTEGER     NOT NULL PRIMARY KEY ,
    code                    TEXT        NOT NULL CHECK (char_length(code)  <= 255) UNIQUE,
    label                   TEXT        NOT NULL CHECK (char_length(label)  <= 255) UNIQUE,
    description             TEXT        NOT NULL CHECK (char_length(description)  <= 510),
    permission_order        INTEGER     NOT NULL,
    permission_category_id  INTEGER     NOT NULL,
    is_workflow_restricted  BOOLEAN     NOT NULL DEFAULT FALSE,
    version                 INTEGER     NOT NULL DEFAULT 1,
    created_by              INTEGER     NOT NULL,
    created_date            TIMESTAMPTZ NOT NULL DEFAULT current_timestamp,
    updated_by              INTEGER     NOT NULL,
    updated_date            TIMESTAMPTZ NOT NULL DEFAULT current_timestamp,
    constraint permission_fk_1 foreign key (permission_category_id) references tims_permission_category(id),
    constraint permission_fk_2 foreign key (created_by) references tims_user(id),
    constraint permission_fk_3 foreign key (updated_by) references tims_user(id)
);



CREATE TABLE IF NOT EXISTS tims_role_permission  (
    role_id                 INTEGER     NOT NULL,
    permission_id           INTEGER     NOT NULL,
    created_by              INTEGER     NOT NULL,
    created_date            TIMESTAMPTZ NOT NULL DEFAULT current_timestamp,
    CONSTRAINT pk_role_permission PRIMARY KEY (role_id, permission_id),
    constraint role_permission_fk_1 foreign key (role_id) references tims_role(id),
    constraint role_permission_fk_2 foreign key (permission_id) references tims_permission(id),
    constraint role_permission_fk_3 foreign key (created_by) references tims_user(id)
);


CREATE TABLE IF NOT EXISTS tims_role_permission_workflow_status  (
    role_id                 INTEGER     NOT NULL,
    permission_id           INTEGER     NOT NULL,
    workflow_status_code    VARCHAR     NOT NULL,
    created_by              INTEGER     NOT NULL,
    created_date            TIMESTAMPTZ NOT NULL DEFAULT current_timestamp,
    CONSTRAINT pk_role_permission_workflow PRIMARY KEY (role_id, permission_id, workflow_status_code),
    constraint role_permission_workflow_fk_1 foreign key (role_id, permission_id) references tims_role_permission(role_id, permission_id),
    constraint role_permission_workflow_fk_2 foreign key (workflow_status_code) references workflow_status(code),
    constraint role_permission_workflow_fk_3 foreign key (created_by) references tims_user(id)
);

